
public class EX02대입연산자 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 29;
		num -= 2+3*4;
		
		System.out.println(num);
		
		int i = 3;
		System.out.println(++i);
		
		int opr = 0;
		opr += 3;
		System.out.println(opr);
		
		System.out.println(opr++);
		System.out.println(opr);
		System.out.println(++opr);
		System.out.println(opr);
	}

}
